    $self->{panel_new_left} = Wx::Panel->new($self->{splitter_new}, wxID_ANY);
    
    $self->{sizer_new_left} = Wx::BoxSizer->new(wxVERTICAL);
    
    $self->{box_new_list} = Wx::StaticBoxSizer->new(Wx::StaticBox->new($self->{panel_new_left}, wxID_ANY, "Items"), wxVERTICAL);
    $self->{sizer_new_left}->Add($self->{box_new_list}, 1, wxALL|wxEXPAND, 6);
    
    $self->{list_new_items} = Wx::ListCtrl->new($self->{box_new_list}->GetStaticBox(), wxID_ANY, wxDefaultPosition, wxDefaultSize, wxBORDER_SUNKEN|wxLC_REPORT);
    $self->{box_new_list}->Add($self->{list_new_items}, 1, wxALL|wxEXPAND, 4);
    
    $self->{panel_new_right} = Wx::Panel->new($self->{splitter_new}, wxID_ANY);
    
    $self->{sizer_new_right} = Wx::BoxSizer->new(wxVERTICAL);
    
    $self->{box_new_details} = Wx::StaticBoxSizer->new(Wx::StaticBox->new($self->{panel_new_right}, wxID_ANY, "Details"), wxVERTICAL);
    $self->{sizer_new_right}->Add($self->{box_new_details}, 1, wxALL|wxEXPAND, 6);
    
    $self->{txt_new_details} = Wx::TextCtrl->new($self->{box_new_details}->GetStaticBox(), wxID_ANY, "", wxDefaultPosition, wxDefaultSize, wxTE_MULTILINE|wxTE_READONLY);
    $self->{box_new_details}->Add($self->{txt_new_details}, 1, wxALL|wxEXPAND, 4);
    
    $self->{panel_new_right}->SetSizer($self->{sizer_new_right});
    
    $self->{panel_new_left}->SetSizer($self->{sizer_new_left});
    
    $self->{splitter_new}->SplitVertically($self->{panel_new_left}, $self->{panel_new_right}, );
    
    $self->{page_new}->SetSizer($self->{sizer_new_root});
    
    $self->{panel_logging_right}->SetSizer($self->{sizer_logging_right});
    
    $self->{panel_logging_left}->SetSizer($self->{sizer_logging_left});
    
    $self->{splitter_logging}->SplitVertically($self->{panel_logging_left}, $self->{panel_logging_right}, );
    
    $self->{page_logging}->SetSizer($self->{sizer_logging_root});
    
    $self->{page_console}->SetSizer($self->{sizer_console_root});
    
    $self->{panel_library_right}->SetSizer($self->{sizer_library_right});
    
    $self->{panel_library_left}->SetSizer($self->{sizer_library_left});
    
    $self->{splitter_library}->SplitVertically($self->{panel_library_left}, $self->{panel_library_right}, );
    
    $self->{page_library}->SetSizer($self->{sizer_library_root});
    
    $self->{page_connections}->SetSizer($self->{sizer_connections_root});
    
    $self->{panel_monitor_right}->SetSizer($self->{sizer_monitor_right});
    
    $self->{panel_monitor_left}->SetSizer($self->{sizer_monitor_left});
    
    $self->{splitter_monitor}->SplitVertically($self->{panel_monitor_left}, $self->{panel_monitor_right}, );
    
    $self->{page_monitor}->SetSizer($self->{sizer_monitor_root});
    
    $self->{panel_search_right}->SetSizer($self->{sizer_search_right});
    
    $self->{panel_search_left}->SetSizer($self->{sizer_search_left});
    
    $self->{splitter_search}->SplitVertically($self->{panel_search_left}, $self->{panel_search_right}, );
    
    $self->{page_search}->SetSizer($self->{sizer_search_root});
    
    $self->{panel_main}->SetSizer($self->{sizer_root});
    
    $self->Layout();
    # end wxGlade

    # in LimeFrame->new(), after SplitVertically calls:
    $self->{splitter_search}->SetSashPosition(240, 1);
    $self->{splitter_monitor}->SetSashPosition(520, 1);
    $self->{splitter_library}->SetSashPosition(220, 1);
    $self->{splitter_logging}->SetSashPosition(220, 1);
    $self->{splitter_new}->SetSashPosition(360, 1);

    return $self;
}


# end of class LimeFrame

