package MyApp;

use base qw(Wx::App);
use strict;

sub OnInit {
    my( $self ) = shift;

    Wx::InitAllImageHandlers();

    my $frame_lime = LimeFrame->new();

    $self->SetTopWindow($frame_lime);
    $frame_lime->Show(1);

    return 1;
}
